public int sum (int a, int b) {
  return a+b;
}





public int average (int a, int b) {
  return (a+b)/2;
}





public int randomNumber (int base) {
  return (Math.random() * base) (int);
}





public int randomNumber (int base, int max) {
  return (Math.random() * (max-base) + max) (int);
}


boolean canSleepIn (boolean isWeekday, boolean onVacation) {
  return !isWeekday || onVacation;
}





public boolean getsSpeedingTicket (double speed, boolean grouchyCop) {
  return (speed > 70.0) || (grouchyCop && speed > 65.0);
}
